        },
        {
            id: 2,
            text: '투두리스트2',
            completed: false,
        },
        {
            id: 3,
            text: '완료1',
            completed: true,
        },
    ]);

    const nextId = useRef(4);

    return (
        <div>
            { tasks.map( task => 
                <Item
                    text = { task.text }
                    completed = { task.completed }
                / >
                )}
        </div>
    );
}

export default TodoList;
